void quick_sort(int q[], int l, int r)
{
    if (l &gt;= r) return;
	int i = l - 1, j = r + 1, x = q[l + r &gt;&gt; 1];
    while (i &lt; j)
    {
        do i ++ ; while (q[i] &lt; x);
        do j -- ; while (q[j] &gt; x);
        if (i &lt; j) swap(q[i], q[j]);
    }
    quick_sort(q, l, j), quick_sort(q, j + 1, r);
}
