void build(int p,int pl,int pr){
	if(pl==pr){
		tree[p]=a[pl];
		return;
	}
	int mid = (pl + pr) &gt;&gt; 1;
	build(ls(p),pl,mid);
	build(rs(p),mid+1,pr);
	tree[p] = tree[ls(p)] + tree[rs(p)];//
}


