vector&lt;int&gt; prefix_function(string s) {
  int n = (int)s.length();
  vector&lt;int&gt; pi(n);
  for (int i = 1; i &lt; n; i++) {
    int j = pi[i - 1];
    while (j &gt; 0 &amp;&amp; s[i] != s[j]) j = pi[j - 1];
    if (s[i] == s[j]) j++;
    pi[i] = j;
  }
  return pi;
}
